# https://adventofcode.com/2022/day/6
#
def solve(k, seq)
    seqc = seq.chars
    n = seqc.length
    (n-k).times do |start|
        if seqc.slice(start, k).uniq.length == k
            return start+k
        end
    end
end

### puzzle input
input = ""
puts "Part 1: #{solve(4, input)}"
puts "Part 2: #{solve(14, input)}"
puts

def check(input, expected, actual)
    if expected != actual
        puts "[#{input}]\n\t-#{expected}\n\t+#{actual}"
    end
end

### sample inputs
[
    ["mjqjpqmgbljsphdztnvjfqwrcgsmlb", 7, 19],
    ["bvwbjplbgvbhsrlpgdmjqwftvncz", 5, 23],
    ["nppdvjthqldpwncqszvftbrmjlhg", 6, 23],
    ["nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg", 10, 29],
    ["zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw", 11, 26],
].each do |input, p1, p2|
    check input, p1, solve(4, input)
    check input, p2, solve(14, input)
end
